import React, { useState } from "react";
import axios from "axios";
const BASE = "api.openweathermap.org/data/2.5/weather?q=";
const API_KEY = "fdecff23904fee4cca33c1d32dd8b12a";
export default function Weather(props) {
  const [weather, setWeather] = useState(true);
  const [query, setQuery] = useState("");

  const search = (evt) => {
    if (evt.key === "Enter") {
      axios
        .get(
          `http://api.weatherapi.com/v1/forecast.json?key=10cae7ec92fa4e47ac933047200611&q=${query}&days=1 `
        )
        .then((res) => {
          const apiData = res.data;
          setWeather(apiData);
          setQuery("");
          console.log(weather);
        })
        .catch((err) => {
          console.log("error");
        });
    }
  };
  //current date
  const buildDate = (d) => {
    let months = [
      "January",
      "Febuary",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    const days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];
    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year} `;
  };

  return (
    <div>
       <div className="search-box">
        <input
          type="text"
          className="search-bar"
          placeholder="Search..."
          onChange={(e) => setQuery(e.target.value)}
          value={query}
          onKeyPress={search}
        ></input>
      </div>
      <div className="location-container">
        <h2>
          {weather.location.name} , {weather.location.region}
        </h2>
      </div>
      <div className="date-container">{buildDate(new Date())}</div>
      <div className="weather-container">
        <h3>Temp: {weather.current.temp_f}f</h3>
      </div>  
    </div>

  );
}
