import React, {  useState } from "react";
import axios from "axios";
const BASE = "api.openweathermap.org/data/2.5/";
const API_KEY = "fdecff23904fee4cca33c1d32dd8b12a";
export default function Weather(props) {
  const [weather, setWeather] = useState(true);
  const [query,setQuery] = useState('');

  const search = evt => {
    if(evt.key === 'Enter'){
      axios
      .get(
        `${BASE}weather?q=${query}&units=imperial&APPID=${API_KEY} `
      )
      .then((res) => {
        const apiData = res.data;
        setWeather(apiData);
        setQuery('')
      })
      .catch((err) => {
        console.log("error");
      });
    }
  }
  //current date
  const buildDate = (d) => {
    let months = [
      "January",
      "Febuary",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    const days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];
    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year} `;
  };

