  //   axios
  //     .get(
  //       `http://api.weatherapi.com/v1/current.json?key=10cae7ec92fa4e47ac933047200611&q=pittsburgh`
  //     )
  //     .then((res) => {
  //       const weatherRes = res.data.current;
  //       setWeather(weatherRes);
  //     })
  //     .catch((err) => {
  //       console.log("error");
  //     });
  // }, []);

  return (
    <div>
      <div className="search-box">
        <input
          type="text"
          className="search-bar"
          placeholder="Search..."
          onChange={e => setQuery(e.target.value)}
          value={query}
          onKeyPress={search}
        ></input>
      </div>
      <div className="location-container">

      </div>
      <div className="date-container">{buildDate(new Date())}</div>
      <div className='weather-container'></div>
    </div>
  );
}
