import React, { useEffect, useState } from "react";
import axios from "axios";
const BASE = 'api.openweathermap.org/data/2.5/'
const API_KEY = 'fdecff23904fee4cca33c1d32dd8b12a'
export default function Weather(props) {
  const [location, setLocation] = useState(true);
  const [weather, setWeather] = useState(true);

const buildDate = (d) => {
  let months = ['January', 'Febuary', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
  const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday']
}

  //Weather data from the API
  useEffect(() => {
    axios
      .get(
        `http://api.weatherapi.com/v1/current.json?key=10cae7ec92fa4e47ac933047200611&q=pittsburgh`
      )
      .then((res) => {
        const locationRes = res.data.location;
        setLocation(locationRes);
      })
      .catch((err) => {
        console.log("error");
      });
  }, []);
  useEffect(() => {
    axios
      .get(
        `http://api.weatherapi.com/v1/current.json?key=10cae7ec92fa4e47ac933047200611&q=pittsburgh`
      )
      .then((res) => {
        const weatherRes = res.data.current;
        setWeather(weatherRes);
      })
      .catch((err) => {
        console.log("error");
      });
  }, []);

  return (
    <div>
      <div className='search-box'>
      <input type='text' className='search-bar' placeholder='Search...'></input>
      </div>
      <div className='location-container'>
      <h3>
        {location.name} , {location.region}
      </h3>
      </div>
      <p>Time: {location.localtime}</p>
      <p>Tempature: {weather.temp_f}f</p>
    </div>
  );
}
