import React, { useState } from "react";
import axios from "axios";
import styled from "styled-components";
const StyledWeather = styled.div`
h2{
  font-size:1.3rem;

}
p{
  text-align:center;
}
.date-container{
  display:flex;
  justify-content:center;
}
.img-container{
  display:flex;
  justify-content:center;
}

.meme-container{
  display:flex;
}


`;
export default function Weather(props) {
  //current date
  const buildDate = (d) => {
    let months = [
      "January",
      "Febuary",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    const days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];
    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year} `;


  };

  return (
    <StyledWeather>
      <main>
        <div>
          <div className="location-container">
            <h2>
              {props.weather.location.name} , {props.weather.location.region}
            </h2>
          </div>
          <div className="date-container">{buildDate(new Date())}</div>
          {props.weather.current.temp_f <= '40' ? (<div className='meme-container'>
        <img src='https://i.pinimg.com/564x/54/aa/a7/54aaa70af6c704c25b6209899b4c7ccf.jpg' alt='funny weather' />
          </div> ) : (<div></div>)}
          <div className="weather-container">
            <p>Temp: {props.weather.current.temp_f}f</p>
            <p>Condition: {props.weather.current.condition.text}</p>
            <div className='img-container'>
            <img
              src={props.weather.current.condition.icon}
              alt={props.weather.current.condition.code}
            />
            </div>
            <p>Wind MPH: {props.weather.current.wind_mph} mph</p>
            <p>Wind Degree: {props.weather.current.wind_degree} degrees</p>
            <p>Wind Direction: {props.weather.current.wind_dir}</p>
            <p>Humidity: {props.weather.current.humidity} %</p>
            <p>Feels like: {props.weather.current.feelslike_f} f</p>
          </div>
        </div>
      </main>
    </StyledWeather>
  );
}
