import React, { useState } from "react";
import "./App.css";
import Weather from "./components/Weather";
import Search from "./components/Search";
import axios from "axios";
function App() {
  const [query, setQuery] = useState("");
  const [weather, setWeather] = useState({
    name: null,
    location: null,
    temp_f: null,
    text: null,
    icon: null,
    code: null,
    wind_mph: null,
    wind_degree: null,
    wind_dir: null,
  });
  const search = (evt) => {
    if (evt.key === "Enter") {
      axios
        .get(
          `http://api.weatherapi.com/v1/forecast.json?key=10cae7ec92fa4e47ac933047200611&q=${query}&days=1 `
        )
        .then((res) => {
          setQuery("");
          const weatherData = res.data;
          setWeather(weatherData);
        })
        .catch((err) => {
          console.log("error");
        });
    }
  };
  console.log(weather);
  return (
    <div className="App">
      <h2>My Weather</h2>
      <div className="search-box">
        <input
          type="text"
          className="search-bar"
          placeholder="Search..."
          onChange={(e) => setQuery(e.target.value)}
          value={query}
          onKeyPress={search}
        ></input>
      </div>
      {weather.temp === null ? (<p>No weather to display</p>) : <Weather weather={weather}/>}
    </div>
  );
}

export default App;
