import React, { useState } from "react";
import axios from "axios";
const BASE = "api.openweathermap.org/data/2.5/weather?q=";
const API_KEY = "fdecff23904fee4cca33c1d32dd8b12a";
export default function Weather(props) {
  const [weather, setWeather] = useState(true);
  const [query, setQuery] = useState("");

  const search = (evt) => {
    if (evt.key === "Enter") {
      axios
        .get(
          `http://api.weatherapi.com/v1/forecast.json?key=10cae7ec92fa4e47ac933047200611&q=${query}&days=1 `
        )
        .then((res) => {
          const apiData = res.data;
          setWeather(apiData);
          setQuery("")
        })
        .catch((err) => {
          console.log("error");
        });
    }
  };
  //current date
  const buildDate = (d) => {
    let months = [
      "January",
      "Febuary",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    const days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday",
    ];
    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year} `;
  };
  if(!weather.main){
    return (
      <div>
        <main>
          <div className="search-box">
            <input
              type="text"
              className="search-bar"
              placeholder="Search..."
              onChange={(e) => setQuery(e.target.value)}
              value={query}
              onKeyPress={search}
            ></input>
          </div>

              <div className="location-container">
                <h2>
                  {weather.location.name} , {weather.location.region}
                </h2>
              </div>
              <div className="date-container">{buildDate(new Date())}</div>
              <div className="weather-container">
                <p>Temp: {weather.current.temp_f}f</p>
                <p>Condition: {weather.current.condition.text}</p>
                <img
                  src={weather.current.condition.icon}
                  alt={weather.current.condition.code}
                />
                <p>Wind MPH: {weather.current.wind_mph} mph</p>
                <p>Wind Degree: {weather.current.wind_degree} degrees</p>
                <p>Wind Direction: {weather.current.wind_dir}</p>
              </div>

        </main>
      </div>
    );
  }

  return (
    <div>
      <main>
        <div className="search-box">
          <input
            type="text"
            className="search-bar"
            placeholder="Search..."
            onChange={(e) => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          ></input>
        </div>

            <div className="location-container">
              <h2>
                {weather.location.name} , {weather.location.region}
              </h2>
            </div>
            <div className="date-container">{buildDate(new Date())}</div>
            <div className="weather-container">
              <p>Temp: {weather.current.temp_f}f</p>
              <p>Condition: {weather.current.condition.text}</p>
              <img
                src={weather.current.condition.icon}
                alt={weather.current.condition.code}
              />
              <p>Wind MPH: {weather.current.wind_mph} mph</p>
              <p>Wind Degree: {weather.current.wind_degree} degrees</p>
              <p>Wind Direction: {weather.current.wind_dir}</p>
            </div>

      </main>
    </div>
  );
}
